$(function(){var c=100;var a=20;var b=600;var d=300;$(".scroll-fade-row").css({opacity:0});$(".scroll-fade-row").children().each(function(){if(windowWidth<=900){$(this).css({opacity:0,transform:"translateY("+a+"px)",transition:d+"ms"})}else{$(this).css({opacity:0,transform:"translateY("+a+"px)",transition:b+"ms"})}});$(".scroll-fade-col").css({opacity:0});$(".scroll-fade-col").children().each(function(){if(windowWidth<=900){$(this).css({opacity:0,transform:"translateX("+a+"px)",transition:d+"ms"})}else{$(this).css({opacity:0,transform:"translateX("+a+"px)",transition:b+"ms"})}});$(window).on("scroll load",function(){var f=$(this).scrollTop();var g=f+$(this).height();var e=g-c;$(".scroll-fade-row").each(function(){var h=$(this).offset().top;if(e>h){$(this).css({opacity:1,transform:"translateY(0)"});$(this).children().each(function(j){$(this).delay(100+j*200).queue(function(){$(this).css({opacity:1,transform:"translateY(0)"}).dequeue()})})}});$(".scroll-fade-col").each(function(){var h=$(this).offset().top;if(e>h){$(this).css({opacity:1,transform:"translateX(0)"});$(this).children().each(function(j){$(this).delay(100+j*200).queue(function(){$(this).css({opacity:1,transform:"translateX(0)"}).dequeue()})})}})})});